// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "../lib/openzeppelin-community-contracts/contracts/account/AccountCore.sol";
import {P256} from "@openzeppelin/contracts/utils/cryptography/P256.sol";

contract CarmelAccount is Initializable, AccountCore {
    address public entryPointAddress;

    bytes32 private _qx;
    bytes32 private _qy;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address ep
    ) initializer public {
        entryPointAddress = ep;
    }

    function _signableUserOpHash(
        PackedUserOperation calldata userOp,
        bytes32 userOpHash
    ) override internal view virtual returns (bytes32) {

    }

    function _rawSignatureValidation(bytes32 hash, bytes calldata signature) internal view virtual override returns (bool) {
        return verify(hash, signature);
    }

    function entryPoint() override public view virtual returns (IEntryPoint) {
        return IEntryPoint(entryPointAddress);
    }

    function setKey(bytes32 qx, bytes32 qy) public {
        _qx = qx;
        _qy = qy;
    }

    function verify(bytes32 hash, bytes calldata signature) public view returns (bool) {
        if (signature.length < 0x40 || _qx == 0 || _qy == 0) return false;

        bytes32 r = bytes32(signature[0x00:0x20]);
        bytes32 s = bytes32(signature[0x20:0x40]);

        return P256.verify(hash, r, s, _qx, _qy);
    }
}

