pragma solidity ^0.8.28;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/utils/Create2.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";
// import "@account-abstraction/contracts/core/SenderCreator.sol";
// import {IEntryPoint} from "@openzeppelin/contracts/interfaces/draft-IERC4337.sol";
// import {SenderCreator} from "@account-abstraction/contracts/core/SenderCreator.sol";
// import {EntryPoint} from "@account-abstraction/contracts/core/EntryPoint.sol";

import './CarmelAccount.sol';

interface ICarmelVerifier {
    function verify(bytes memory authenticatorData, string memory clientDataJSON, uint256 r, uint256 s, uint256 x, uint256 y) external view returns (bool);
}

/// @title The Carmel Registry
/// @author I. Dan Calinescu
/// @notice This keeps track of all Carmel Accounts and associated data such as public keys
/// @custom:oz-upgrades-unsafe-allow external-library-linking
contract CarmelRegistry is Initializable {
    CarmelAccount public accountImpl;

    /// @dev Constants used to track permissions
    uint8 constant private ADMIN_PERM = 250;
    uint8 constant private START_PERM = 1;

    /// @dev The address of the EntryPoint contract for abstract accounts
    address public entryPointAddress;

    /// @notice This represents an individual Carmel Signature
    struct Signature {
        bytes32 username; 
        uint256 keyId;
        uint256 r;
        uint256 s;
        bytes authenticatorData;
        string clientDataJSON;
    }

    /// @notice This represents an individual Carmel Account
    struct Account {
        uint256 id;
        bytes32 username;
        bytes32 sysmeta0;
        bytes32 sysmeta1;
        bytes32 umeta0;
        bytes32 umeta1;
        bytes32 group;
        uint32 total_keys;
        uint32 total_addresses;
    }

    /// @notice The total accounts available in this registry
    uint256 public total_accounts;

    /// @dev Keep track of the main owner of this registry
    address public owner;

    /// @dev The address of the Carmel Verifier used to verify signatures
    address public verifier;

    mapping(address => uint8) public perms;
    mapping(bytes32 => Account) public accounts;
    mapping(bytes32 => bytes32[]) public usernames;
    mapping(bytes32 => uint256[]) public keys_x;
    mapping(bytes32 => uint256[]) public keys_y;
    mapping(bytes32 => address[]) public addresses;

    /// @notice Helper to ensure admin permissions on a function
    modifier requireAdmin() {
      require(perms[msg.sender] >= ADMIN_PERM, "Admin level required");
      _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function version() public pure returns (uint16) {
        return 1;
    }

    function initialize(
        address sender,
        address ep,
        address ver
    ) initializer public {
        perms[msg.sender] = ADMIN_PERM;
        owner = sender;
        entryPointAddress = ep;
        verifier = ver;
        accountImpl = new CarmelAccount();
    }

    /// @notice Register a brand new Carmel account into this registry
    /// @param username The Carmel username (must be unique)
    /// @param grp The Carmel group to which this username will be added (ex: "users")
    /// @param addr A default address associated with this account
    /// @param sh The system-owned IPFS hash representing system data associated with this account 
    /// @param uh The user-owned IPFS hash representing user data associated with this account 
    function register (bytes32 username, bytes32 grp, address addr, bytes32[2] memory sh, bytes32[2] memory uh) public requireAdmin {
        Account memory account = accounts[username];
        require(account.username == bytes32(0), "The username already exists");

        Account memory acct = Account({ id: total_accounts, group: grp, username: username, sysmeta0: sh[0], sysmeta1: sh[1], umeta0: uh[0], umeta1: uh[1], total_keys: 0, total_addresses: 1 });
        accounts[username] = acct;

        addresses[username].push(addr);
        usernames[grp].push(username);

        perms[addr] = START_PERM;
        total_accounts = total_accounts + 1;

        createAccount(addr, 0);
    }

    /// @notice Get a list of all accounts within a particular group
    /// @param grp The group to look up
    /// @return All the accounts within the group
    function group(bytes32 grp) public view returns (Account[] memory) {
        bytes32[] memory all = usernames[grp];
        uint total = all.length;
        Account[] memory accts = new Account[](total);

        for(uint i = 0; i < total; i++) {
            accts[i] = accounts[all[i]];
        }

        return accts;
    }

    function updatePerms (address addr, uint8 level) public requireAdmin {
        perms[addr] = level;
    }

    /// @notice Add a new public key associated with an account
    /// @param username The account username
    /// @param x The x coordinate of the key
    /// @param y The y coordinate of the key
    function addKey (bytes32 username, uint256 x, uint256 y) public requireAdmin {
        Account memory account = accounts[username];
        require(account.username != bytes32(0), "The account does not exist");

        keys_x[username].push(x);
        keys_y[username].push(y);
        accounts[username].total_keys = account.total_keys + 1;
    }

    /// @notice Sign with 
    function sign (Signature memory signature) public view requireAdmin returns(bool) {
        Account memory account = accounts[signature.username];

        require(account.username != bytes32(0), "The account does not exist");
        require(keys_x[signature.username].length > signature.keyId && keys_y[signature.username].length > signature.keyId, "Invalid device");

        uint256 x = keys_x[signature.username][signature.keyId];
        uint256 y = keys_y[signature.username][signature.keyId];

        return ICarmelVerifier(verifier).verify(signature.authenticatorData, signature.clientDataJSON, signature.r, signature.s, x, y);
    }

    /// @notice
    function getAddress(address owner, uint256 salt) internal view returns (address) {
        return Create2.computeAddress(bytes32(salt), keccak256(abi.encodePacked(
                type(ERC1967Proxy).creationCode,
                abi.encode(
                    address(accountImpl),
                    abi.encodeCall(CarmelAccount.initialize, (entryPointAddress))
                )
            )));
    }

    /// @notice
    function createAccount(address owner, uint256 salt) internal returns (CarmelAccount ret) {
        // require(msg.sender == address(senderCreator), "only callable from SenderCreator");
        address addr = getAddress(owner, salt);
        uint256 codeSize = addr.code.length;

        if (codeSize > 0) {
            return CarmelAccount(payable(addr));
        }

        ret = CarmelAccount(payable(new ERC1967Proxy{salt : bytes32(salt)}(
                address(accountImpl),
                abi.encodeCall(CarmelAccount.initialize, (entryPointAddress))
        )));

        return ret;
    }
}

